$(document).ready(function () {

	MATERIAL_ICONS.forEach(function (icon) {
		$('.icons').append($('<i class="iconbutton material-icons">' + icon + '</i>'))
	});

	$('.selected-icon-container').click(function (e) {
		var icons_container = $(e.target).parent().parent().find(".icons");
		console.log(icons_container);
		if (icons_container.is(':hidden')) {
			icons_container.css('display', 'flex');
		}
		else
			icons_container.css('display', 'none');

	});

	$('.selected-color').colorPicker({
		customBG: '#222',
		margin: '4px -2px 0',
		opacity: false,
		buildCallback: function ($elm) {
			var colorInstance = this.color,
				colorPicker = this;

			$elm.prepend('<div class="cp-panel">' +
				'R <input type="text" class="cp-r" /><br>' +
				'G <input type="text" class="cp-g" /><br>' +
				'B <input type="text" class="cp-b" /><hr>' +
				'H <input type="text" class="cp-h" /><br>' +
				'S <input type="text" class="cp-s" /><br>' +
				'B <input type="text" class="cp-v" /><hr>' +
				'<input type="text" class="cp-HEX" />' +
				'</div>').on('change', 'input', function (e) {
					var value = this.value,
						className = this.className,
						type = className.split('-')[1],
						color = {};

					color[type] = value;
					colorInstance.setColor(type === 'HEX' ? value : color,
						type === 'HEX' ? 'HEX' : /(?:r|g|b)/.test(type) ? 'rgb' : 'hsv');
					colorPicker.render();
					this.blur();
				});
		},

		cssAddon: // could also be in a css file instead
			'.cp-color-picker{box-sizing:border-box; width:226px;}' +
			'.cp-color-picker .cp-panel {line-height: 21px; float:right;' +
			'padding:0 1px 0 8px; margin-top:-1px; overflow:visible}' +
			'.cp-xy-slider:active {cursor:none;}' +
			'.cp-panel, .cp-panel input {color:#bbb; font-family:monospace,' +
			'"Courier New",Courier,mono; font-size:12px; font-weight:bold;}' +
			'.cp-panel input {width:28px; height:12px; padding:2px 3px 1px;' +
			'text-align:right; line-height:12px; background:transparent;' +
			'border:1px solid; border-color:#222 #666 #666 #222;}' +
			'.cp-panel hr {margin:0 -2px 2px; height:1px; border:0;' +
			'background:#666; border-top:1px solid #222;}' +
			'.cp-panel .cp-HEX {width:44px; position:absolute; margin:1px -3px 0 -2px;}',

		renderCallback: function ($elm, toggled) {
			var colors = this.color.colors.RND,
				modes = {
					r: colors.rgb.r, g: colors.rgb.g, b: colors.rgb.b,
					h: colors.hsv.h, s: colors.hsv.s, v: colors.hsv.v,
					HEX: this.color.colors.HEX
				};

			$('input', '.cp-panel').each(function () {
				this.value = modes[this.className.substr(3)];
			});

			var hex = this.color.colors.HEX; // the RGB color in 0-255);
			$elm.closest(".selector-container").find('i.material-icons.selected-icon').css("background", "#" + hex);
			$('input[name="Cate.Color"]').val("#" + hex);
			$('input[name="Cate.Icon"]').val($(".selector-container").find('i.material-icons.selected-icon').text());
			$('input[name="Maccount.Color"]').val("#" + hex);
			$('input[name="Maccount.Icon"]').val($(".selector-container").find('i.material-icons.selected-icon').text());
		}

	});

	$('.iconbutton').click(function (e) {
		var element = $(e.target);
		console.log(element);
		element.closest(".icons").parent().find('.selected-icon').html(element.html());
		element.closest('.icons').css('display', 'none');
	});

});